import React, { useEffect, useState } from "react";
import { Link, useLocation } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import Crysta from "../Images/cryy.jpg";
import Creta from "../Images/creta.jpeg";
import Etios from "../Images/etios.png";
import Gwagon from "../Images/Gwagon.jpg";
import Gurkha from "../Images/Gurkha.jpg";
import Xuv from "../Images/Xuv500.jpg";
import { fetchAllvehicle, vehicleSelector } from "../api/vehicles";

const CarList = () => {
  const retrievedData = JSON.parse(localStorage.getItem("Selected Data"));
  const { all_vehicle } = useSelector(vehicleSelector);
  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(fetchAllvehicle());
  }, [dispatch]);

  const initialCarData = [
    {
      id: 1,
      name: "Mercedes G-wagon",
      seats: 6,
      price: 5500,
      image: Gwagon,
      seatsLeft: 1,
      Date: "08/01/2025",
      time: "5:15 AM",
      tripDuration: "5h 30m",
      features: [
        "Free Cancellation",
        "Third Party Liability",
        "Instantly Confirmed",
        "Air Conditioned Luxury SUV",
        "Comfortable & Spacious Seating",
        "Female Passenger Safety Standards",
      ],
    },
    {
      id: 2,
      name: "Force Gurkha",
      seats: 6,
      price: 4000,
      image: Gurkha,
      seatsLeft: 2,
      Date: "08/01/2025",
      time: "6:15 AM",
      tripDuration: "5h 30m",
      features: [
        "Free Cancellation",
        "Third Party Liability",
        "Instantly Confirmed",
        "Air Conditioned Luxury SUV",
        "Comfortable & Spacious Seating",
        "Female Passenger Safety Standards",
      ],
    },
  ];

  const [carData, setCarData] = useState(initialCarData);
  const [sortConfig, setSortConfig] = useState({ key: null, ascending: true });

  const handleSort = (key) => {
    const isAscending = sortConfig.key === key ? !sortConfig.ascending : true;
    const sortedData = [...carData].sort((a, b) => {
      if (key === "time") {
        const timeA = new Date(`1970-01-01T${a.time}`);
        const timeB = new Date(`1970-01-01T${b.time}`);
        return isAscending ? timeA - timeB : timeB - timeA;
      } else if (key === "price" || key === "seatsLeft") {
        return isAscending ? a[key] - b[key] : b[key] - a[key];
      }
      return 0;
    });

    setCarData(sortedData);
    setSortConfig({ key, ascending: isAscending });
  };

  const location = useLocation();

  return (
    <div className="flex flex-col lg:flex-row gap-4 px-4 lg:px-16 py-6">
      {/* Main Container */}
      <div className="w-full lg:w-3/4 bg-white bg-opacity-80 backdrop-blur-lg shadow-lg p-6 rounded-lg">
        {/* Tabs */}
        <div className="flex flex-wrap justify-center gap-4 mb-6">
          {[
            { path: "/sharedcabs", label: "Shared Cars" },
            { path: "/fullcars", label: "Full Cars" },
          ].map((tab) => (
            <Link key={tab.path} to={tab.path}>
              <button
                className={`px-4 sm:px-6 py-2 sm:py-3 rounded-lg font-medium transition-all duration-300 shadow-md text-sm sm:text-base ${
                  location.pathname === tab.path
                    ? "bg-indigo-600 text-white shadow-lg transform scale-105"
                    : "bg-gray-300 text-gray-700 hover:bg-gray-400 hover:text-white"
                }`}
              >
                {tab.label}
              </button>
            </Link>
          ))}
        </div>

        {/* Sorting Controls */}
        <div className="w-full grid grid-cols-2 gap-4 bg-gray-100 shadow-md rounded-md p-3 text-center">
          {[
            { key: "seatsLeft", label: "Seats Left" },
            { key: "price", label: "Fare" },
          ].map((header) => (
            <div
              key={header.key}
              onClick={() => handleSort(header.key)}
              className="text-gray-700 font-semibold uppercase text-xs sm:text-sm cursor-pointer flex justify-center items-center hover:text-indigo-600 transition-all"
            >
              {header.label}
              <span className="ml-1">
                {sortConfig.key === header.key
                  ? sortConfig.ascending
                    ? "▲"
                    : "▼"
                  : "⇵"}
              </span>
            </div>
          ))}
        </div>

        {/* Car List */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6 mt-4">
          {carData.map((car) => (
            <div
              key={car.id}
              className="bg-white bg-opacity-90 backdrop-blur-md rounded-lg shadow-lg p-5 flex flex-col border border-gray-200 hover:shadow-xl transition-all"
            >
              {/* Image */}
              <img
                src={car.image}
                alt={car.name}
                className="w-full h-auto object-cover rounded-lg shadow-md"
              />

              {/* Car Details */}
              <div className="mt-4">
                <h3 className="text-lg sm:text-xl font-bold text-gray-800">
                  {car.name}
                </h3>
                <p className="text-gray-600 text-sm sm:text-base">
                  {car.seats} Seater
                </p>
                <ul className="text-gray-500 text-xs sm:text-sm mt-2">
                  {car.features.map((feature, index) => (
                    <li key={index}>• {feature}</li>
                  ))}
                </ul>
              </div>

              {/* Booking Info */}
              <div className="flex flex-col items-center mt-4">
                <h1 className="text-md sm:text-lg font-semibold text-indigo-600">
                  {car.Date}
                </h1>
                <h4 className="text-gray-500 text-xs sm:text-sm">{car.time}</h4>
                <p className="bg-blue-200 text-blue-600 px-3 py-1 mt-3 rounded-full font-semibold text-xs sm:text-sm">
                  Duration: {car.tripDuration}
                </p>
                <p className="text-gray-900 font-bold text-xs sm:text-base">
                  Seats Left: {car.seatsLeft}
                </p>
                <p className="text-xl sm:text-2xl font-bold text-green-500">
                  ₹{car.price}/-
                </p>
                <Link to="/Seats">
                  <button className="mt-4 bg-blue-500 text-white py-2 px-5 rounded-md shadow-lg hover:bg-green-500 transition-all">
                    Select Seat
                  </button>
                </Link>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default CarList;
